import{_ as e,o as t,c as a,a as r}from"./app.aefc483d.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"life/\u5237\u6296\u97F3.md","lastUpdated":1670500651000}'),o={name:"life/\u5237\u6296\u97F3.md"},p=r('<p>\u4ECE\u524D\u542C\u4EBA\u8BF4\uFF1A<br> \u4E2D\u56FD\u4EBA\u5177\u6709\u4E09\u79CD\u535A\u58EB\u8D44\u683C</p><ul><li>\u62FF\u7B77\u5B50\u535A\u58EB</li><li>\u5403\u74DC\u5B50\u535A\u58EB</li><li>\u5237\u6296\u97F3\u535A\u58EB</li></ul><p>\u800C\u4E09\u79CD\u6280\u672F\u4E2D\u6700\u8FDB\u6B65\u6700\u53D1\u8FBE\u7684\uFF0C\u8981\u7B97\u5237\u6296\u97F3\u3002\u8FD1\u6765\u6296\u97F3\u5927\u738B\u7684\u7545\u9500\uFF0C\u4FBF\u662F\u5176\u8001\u5927\u7684\u8BC1\u636E\u3002\u636E\u5173\u5FC3\u6B64\u4E8B\u7684\u4EBA\u8BF4\uFF0C\u6296\u97F3\u5927\u738B\u4E00\u7C7B\u7684\u89C6\u9891 APP\uFF0C\u6700\u8FD1\u5E02\u4E0A\u6D41\u884C\u7684\u6709\u8BB8\u591A\u79CD\u7C7B\u3002\u6700\u521D\u662F\u67D0\u5927\u516C\u53F8\u300C\u7528\u63A8\u8350\u7B97\u6CD5\u300D \u521B\u5236\u7684\uFF0C\u540E\u6765\u6709\u4EC0\u4E48\u300C\u67D0\u624B\u516C\u53F8\u300D\u3001\u300C\u67D0\u7EA2\u4E66\u516C\u53F8\u300D......\u7B49\u79CD\u79CD\u51FA\u54C1\u9646\u7EED\u4EA7\u51FA\u3002\u5230\u73B0\u5728\u5DEE\u4E0D\u591A\u65E0\u8BBA\u54EA\u4E2A\u7A77\u4E61\u50FB\u5904\u7684\u5C0F\u4EA7\u54C1\u4E0A\uFF0C\u90FD\u6709\u51E0 TB \u7684\u89C6\u9891\u9648\u5217\u800C\u503E\u9500\u7740\u4E86\u3002\u73B0\u4EE3\u4E2D\u56FD\u4EBA\u7684\u7CBE\u901A\u5237\u6296\u97F3\u672F\uFF0C\u7531\u6B64\u76D6\u53EF\u60F3\u89C1\u3002</p><p>\u6211\u5BF9\u4E8E\u6B64\u9053\uFF0C\u4E00\u5411\u975E\u5E38\u77ED\u62D9\uFF0C\u8BF4\u51FA\u6765\u6709\u4F24\u4E8E\u4E2D\u56FD\u4EBA\u7684\u4F53\u9762\uFF0C\u4F46\u5BF9\u81EA\u5BB6\u4EBA\u4E0D\u59A8\u8C08\u8C08\u3002\u6211\u4ECE\u6765\u4E0D\u66FE\u4E3B\u52A8\u7684\u627E\u6C42\u67D0\u97F3\u6765\u5237\u3002\u4F46\u5230\u4EBA\u5BB6\u4F5C\u5BA2\uFF0C\u53D7\u4EBA\u529D\u8BF1\u65F6\uFF1B\u6216\u8005\u5728\u9152\u5E2D\u4E0A\u3001\u91CD\u5E86\u7684\u8336\u697C\u4E0A\uFF0C\u4E5F\u4F1A\u4E0D\u7531\u81EA\u4E3B\u7684\u628A\u624B\u673A\u62FF\u51FA\u6765\uFF0C\u800C\u5F53\u53D1\u73B0\u4E00\u4E2A\u6D88\u606F\u4E5F\u6CA1\u6709\u540E\uFF0C\u4E5F\u4FBF\u70B9\u5F00\u6296\u97F3\u6ED1\u4E86\u8D77\u6765\u3002\u6211\u5FC5\u987B\u6CE8\u610F\u9009\u62E9\uFF0C\u9009\u90A3\u8F83\u77ED\u3001\u641E\u7B11\u3001\u800C\u6216\u4E61\u6751\u7530\u91CE\u7684\u89C6\u9891\uFF0C\u800C\u5BF9\u4E8E\u90A3\u4E9B\u5947\u88C5\u5F02\u670D\u7684\u3001\u597D\u55E8\u54DF\u4E00\u7C7B\u7684\uFF0C\u6211\u591A\u534A\u662F\u62D2\u7EDD\u7684\u3002\u800C\u6BCF\u6BCF\u5230\u4E86\u996D\u70B9\uFF0C\u6216\u88AB\u4EBA\u60CA\u6270\u65F6\uFF0C\u624D\u53D1\u73B0\u5DF2\u7ECF\u8FC7\u4E86\u534A\u5C0F\u65F6\u3002\u78B0\u5230\u8FD9\u79CD\u65F6\u5019\uFF0C\u6211\u5C31\u4E0B\u4E2A\u51B3\u5FC3\uFF0C\u4ECE\u6B64\u6212\u7EDD\u6296\u97F3\u3002\u6212\u7EDD\u4E4B\u6CD5\uFF0C\u5927\u62B5\u662F\u7AD9\u8D77\u8EAB\u6765\uFF0C\u559D\u4E00\u53E3\u8336\u6F31\u4E00\u6F31\u53E3\uFF0C\u70B9\u8D77\u4E00\u652F\u9999\u70DF\uFF0C\u6216\u8005\u628A\u624B\u673A\u63A8\u5F00\u4E9B\uFF0C\u4EE5\u6B64\u4E0D\u518D\u4E0E\u5B83\u53D1\u751F\u5173\u7CFB\u3002\u7136\u800C\u8FC7\u4E86\u51E0\u5206\u949F\uFF0C\u4E0E\u522B\u4EBA\u8C08\u4E86\u51E0\u53E5\u8BDD\uFF0C\u4E0D\u77E5\u4E0D\u89C9\u4E4B\u95F4\uFF0C\u624B\u673A\u5C31\u5DF2\u7ECF\u5728\u624B\u4E0A\u4E86\u3002\u7B49\u5230\u81EA\u5DF1\u89C9\u5BDF\u7834\u6212\u7684\u65F6\u5019\uFF0C\u5F80\u5F80\u5DF2\u7ECF\u770B\u4E86\u597D\u51E0\u4E2A\u89C6\u9891\uFF0C\u800C\u8FD9\u65F6\u5019\u4F1A\u60F3\uFF0C\u4E0D\u8981\u7D27\u7684\u3001\u5728\u5237\u51E0\u5206\u949F\uFF0C\u540E\u9762\u518D\u6212\u7684\u4E86\u3002\u8FD9\u6837\uFF0C\u5403\u4E86\u975E\u6212\u4E0D\u53EF\uFF0C\u6212\u4E86\u975E\u5403\u4E0D\u53EF\uFF1B\u5403\u800C\u590D\u6212\uFF0C\u6212\u800C\u590D\u5403\uFF0C\u6211\u4E3A\u5B83\u53D7\u5C3D\u82E6\u75DB\u3002\u8FD9\u4F7F\u6211\u73B0\u5728\u60F3\u8D77\u4E86\u6296\u97F3\u89C9\u5F97\u5BB3\u6015\u3002</p><p>\u4F46\u6211\u770B\u522B\u4EBA\uFF0C\u7CBE\u901A\u6B64\u6280\u7684\u5F88\u591A\u3002\u6211\u4EE5\u4E3A\u4E2D\u56FD\u4EBA\u4E09\u79CD\u535A\u58EB\u7684\u624D\u80FD\u4E2D\uFF0C\u5237\u6296\u97F3\u7684\u624D\u80FD\u6700\u53EF\u53F9\u4F69\u3002\u5E38\u89C1\u95F2\u6563\u7684\u5C11\u7237\u4EEC\uFF0C\u4E00\u53EA\u624B\u6307\u95F4\u5939\u7740\u4E00\u652F\u9999\u70DF\uFF0C\u4E00\u53EA\u624B\u63E1\u7740\u4E00\u4E2A\u624B\u673A\uFF0C\u70DF\u96FE\u7F2D\u7ED5\uFF0C\u4E14\u6ED1\u4E14\u7B11\uFF0C\u4E14\u7B11\u4E14\u8D70\u3002\u4ECE\u5BB9\u81EA\u7531\uFF0C\u771F\u662F\u300C\u4EA4\u5173\u5199\u610F\u300D\uFF01\u4ED6\u4EEC\u7CBE\u5FC3\u7684\u6311\u9009\u81EA\u5DF1\u559C\u6B22\u7684\u89C6\u9891\uFF0C\u9047\u5230\u4E0D\u6EE1\u610F\u7684\uFF0C\u53EA\u6D88\u300C\u683C\u300D\u5730\u4E00\u6ED1\uFF0C\u518D\u628A\u5634\u91CC\u7684\u70DF\u300C\u556A\u300D\u5730\u4E00\u5410\uFF0C\u800C\u5728\u90A3\u91CC\u56BC\u98DF\u7740\u65B0\u7684\u98DF\u7CAE\u4E86\u3002\u90A3\u624B\u6307\u771F\u8C61\u4E00\u5177\u7CBE\u5DE7\u7075\u654F\u7684\u673A\u5668\uFF0C\u4E0D\u7EDD\u5730\u585E\u8FDB\u89C6\u9891\u53BB\uFF0C\u4E0D\u7EDD\u5730\u5480\u56BC\u7740\uFF0C\u2026\u2026\u5168\u4E0D\u8D39\u529B\uFF0C\u53EF\u4EE5\u6C38\u65E0\u7F62\u4F11\u3002</p><p>\u53D1\u660E\u5237\u6296\u97F3\u7684\u4EBA\uFF0C\u771F\u662F\u4E00\u4E2A\u4E86\u4E0D\u8D77\u7684\u5929\u624D\uFF01\u8FD9\u662F\u4E00\u79CD\u6700\u6709\u6548\u7684\u300C\u6D88\u95F2\u300D\u6CD5\u3002\u8981\u300C\u6D88\u78E8\u5C81\u6708\u300D\uFF0C\u9664\u4E86\u62BD\u9E26\u7247\u4EE5\u5916\uFF0C\u6CA1\u6709\u6BD4\u5237\u6296\u97F3\u66F4\u597D\u7684\u65B9\u6CD5\u4E86\u3002</p><p>\u5728\u4E16\u95F4\u4E00\u5207\u4E8B\u7269\u4E4B\u4E2D\uFF0C\u60F3\u6765\u60F3\u53BB\uFF0C\u53EA\u6709\u6296\u97F3\u3002\u6240\u4EE5\u6211\u8BF4\u53D1\u660E\u5237\u6296\u97F3\u7684\u4EBA\u662F\u4E86\u4E0D\u8D77\u7684\u5929\u624D\u3002\u800C\u80FD\u5C3D\u91CF\u5730\u4EAB\u7528\u6296\u97F3\u7684\u4E2D\u56FD\u4EBA\uFF0C\u5728\u6D88\u95F2\u4E00\u9053\u4E0A\uFF0C\u771F\u662F\u4E86\u4E0D\u8D77\u7684\u79EF\u6781\u7684\u5B9E\u884C\u5BB6\uFF01\u8BD5\u770B\u7406\u53D1\u5E97\u3001\u5730\u94C1\u4E0A\u6296\u97F3\u5927\u738B\u7684\u7545\u9500\uFF0C\u8BD5\u542C\u5C0F\u9762\u9986\u91CC\u3001\u516C\u4EA4\u8F66\u4E0A\u3001\u5BB6\u5EAD\u4E2D\u6EE1\u5C4B\u7684\u6D41\u884C\u97F3\u4E50\uFF0C\u4FBF\u53EF\u60F3\u89C1\u4E2D\u56FD\u4EBA\u5728\u6B64\u4E4B\u9053\u4E0A\u6D88\u78E8\u53BB\u7684\u65F6\u95F4\uFF0C\u6BCF\u5E74\u7EDF\u8BA1\u8D77\u6765\u4E3A\u6570\u4E00\u5B9A\u53EF\u60CA\u3002\u5C06\u6765\u6B64\u9053\u53D1\u5C55\u8D77\u6765\uFF0C\u6050\u6015\u662F\u5168\u4E2D\u56FD\u4E5F\u53EF\u6D88\u706D\u5728\u6717\u6717\u7B11\u58F0\u4E2D\u5462\u3002</p><p>\u6211\u672C\u6765\u89C1\u6296\u97F3\u5BB3\u6015\uFF0C\u5199\u5230\u8FD9\u91CC\uFF0C\u89C9\u5F97\u66F4\u52A0\u5BB3\u6015\u4E86\u3002</p><ul><li><a href="https://baike.baidu.com/item/%E5%90%83%E7%93%9C%E5%AD%90/698877" target="_blank" rel="noreferrer">\u5403\u74DC\u5B50</a></li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>\u672C\u6587\u8F6C\u8F7D\u81EA\uFF1A<a href="https://godruoyi.com/posts/TikTok" target="_blank" rel="noreferrer">\u5237\u6296\u97F3</a></p></div>',10),l=[p];function s(_,i,c,n,d,f){return t(),a("div",null,l)}const T=e(o,[["render",s]]);export{u as __pageData,T as default};
