import{_ as t,o as e,c as r,S as a}from"./chunks/framework.8b0da900.js";const _=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"programe/API 授权为啥要在 Authorization 标头里加个 Bearer.md","filePath":"programe/API 授权为啥要在 Authorization 标头里加个 Bearer.md","lastUpdated":1670500651000}'),n={name:"programe/API 授权为啥要在 Authorization 标头里加个 Bearer.md"},s=a('<p>设计 API 授权，或者调用第三方 API 时，经常会接触到：</p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-light"><code><span class="line"><span style="color:#24292e;">Authorization : Bearer Tokenxxxxxx</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>有没有疑惑为何不直接写成这样就得了：</p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-light"><code><span class="line"><span style="color:#24292e;">Authorization : Tokenxxxxxx</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>这是因为 W3C 的 <a href="https://www.rfc-editororg/rfc/rfc1945" target="_blank" rel="noreferrer">HTTP 1.0</a> 规范，Authorization 的格式是：</p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-light"><code><span class="line"><span style="color:#24292e;">Authorization: &lt;type&gt; &lt;authorization-parameters&gt;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>所以 Bearer 是授权的类型，常见的授权类型还有：</p><table><thead><tr><th style="text-align:center;">授权类型</th><th style="text-align:center;">说明</th></tr></thead><tbody><tr><td style="text-align:center;"><strong>Basic</strong></td><td style="text-align:center;">用于 http-basic 认证</td></tr><tr><td style="text-align:center;"><strong>Bearer</strong></td><td style="text-align:center;">常见于 OAuth 和 JWT 授权</td></tr><tr><td style="text-align:center;"><strong>Digest</strong></td><td style="text-align:center;">MD5 哈希的 http-digest 认证（已弃用）</td></tr><tr><td style="text-align:center;"><strong>HOBA</strong></td><td style="text-align:center;">HTTP 消息签名认证（已弃用）</td></tr><tr><td style="text-align:center;"><strong>Mutual</strong></td><td style="text-align:center;">HTTP 消息签名认证（已弃用）</td></tr><tr><td style="text-align:center;"><strong>Negotiate</strong></td><td style="text-align:center;"><a href="https://tools.ietf.org/html/rfc4559" target="_blank" rel="noreferrer">SPNEGO</a> 认证（已弃用）</td></tr><tr><td style="text-align:center;"><strong>SCRAM-SHA-1</strong></td><td style="text-align:center;">SCRAM-SHA-1 认证（已弃用）</td></tr><tr><td style="text-align:center;"><strong>SCRAM-SHA-256</strong></td><td style="text-align:center;">SCRAM-SHA-256 认证（已弃用）</td></tr><tr><td style="text-align:center;"><strong>vapid</strong></td><td style="text-align:center;">VAPID 认证</td></tr><tr><td style="text-align:center;"><strong>AWS4-HMAC-SHA256</strong></td><td style="text-align:center;"><a href="https://docs.aws.amazon.com/AmazonS3/latest/API/sigv4-auth-using-authorization-header.html" target="_blank" rel="noreferrer">AWS</a> 认证</td></tr><tr><td style="text-align:center;"><strong>AWS4-HMAC-SHA256-PAYLOAD</strong></td><td style="text-align:center;"><a href="https://docs.aws.amazon.com/AmazonS3/latest/API/sigv4-auth-using-authorization-header.html" target="_blank" rel="noreferrer">AWS</a> 认证</td></tr><tr><td style="text-align:center;"><strong>MAC</strong></td><td style="text-align:center;"><a href="https://tools.ietf.org/html/rfc2617#section-3" target="_blank" rel="noreferrer">MAC</a> 认证</td></tr><tr><td style="text-align:center;"><strong>Netrc</strong></td><td style="text-align:center;"><a href="https://tools.ietf.org/html/rfc1738#section-10" target="_blank" rel="noreferrer">Netrc</a> 认证</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>本文转载自：<a href="https://learnku.com/users/1" target="_blank" rel="noreferrer">Summer</a> - <a href="https://learnku.com/articles/68715" target="_blank" rel="noreferrer">API 授权为啥要在 Authorization 标头里加个 Bearer？</a></p></div>',9),l=[s];function o(i,d,c,g,p,h){return e(),r("div",null,l)}const A=t(n,[["render",o]]);export{_ as __pageData,A as default};
